#include <bits/stdc++.h>
using namespace std;

#define mx 100000
bool prime[mx];

void sw() {
    prime[0] = prime[1] = true;
    for (int i = 2; i * i < mx; i++) {
        if (prime[i] == false) {
            for (int j = i * i; j < mx; j+=i) {
                prime[j] = true;
            }
        }
    }
}

int solve() {
    sw();
    int n;
    cin >> n;
    vector<int>prim;
    for (int i = 2; i <= n; i++) {
        if (prime[i] == false) {
            prim.push_back(i);
        }
    }
    int m = prim.size();
    int cnt = 0;
    for (int i = 0; i < m; i++) {
        for (int j = i; j < m; j++) {
            if (prim[i] + prim[j] == n) {
                cnt++;
            }
        }
    }
    if (n % 2 == 0 && prime[int(n / 2)] == false)
        return cnt * 2 - 1;
    return cnt * 2;
}

int main() {
    int t = 1;
    while (t--) cout << solve() << endl;
    return 0;
}
