#include <bits/stdc++.h>
using namespace std;

int getLowValue(set<int>& lowValue, int& n)
{
	auto it = lowValue.lower_bound(n);

	--it;

	return (*it);
}

int mx(int arr[], int n)
{
	int maxSuffArr[n + 1];

	maxSuffArr[n] = 0;

	for (int i = n - 1; i >= 0; --i)
		maxSuffArr[i] = max(maxSuffArr[i + 1], arr[i]);

	int ans = 0;

	set<int> lowValue;

	lowValue.insert(INT_MIN);

	for (int i = 0; i < n - 1; ++i) {
		if (maxSuffArr[i + 1] > arr[i]) {
			ans = max(ans, getLowValue(lowValue,
									arr[i])
							+ arr[i] + maxSuffArr[i + 1]);

			lowValue.insert(arr[i]);
		}
	}
	return ans;
}

int main()
{
    int n;
    scanf("%d", &n);
    int arr[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &arr[i]);
    }

	cout << mx(arr, n);
	return 0;
}
