try:
	w = input()
	d = {'a': 'z', 'A':'Z', 'z':'a', 'Z':'A'}
	cnt = ''
	arr = []
	for i in w:
		if 'a' <= i <= 'z' or 'A' <= i <= 'Z':
			if i in 'azAZ':
				cnt += 2*d[i]
			else:
				cnt += chr(ord(i)+1)
				cnt += chr(ord(i)-1)
		else:
			if len(cnt) != 0:
				arr += [cnt]
			arr.append(i)
			cnt = ''
	if len(cnt) != 0:
		arr += [cnt]
	cnt = ''
	for i in arr:
		if i.isalpha():
			cnt = cnt + i[::-1]
		else:
			cnt = cnt + i
	print(cnt,end=' ')
except:
	print(' ')
