def solve(w):
    if w[0] != '-':
        return w
    if len(w) == 2:
        return w
    x = w[:2]
    y = w[2:]
    x1 = float(x) - float(y)
    x2 = float(x) * float(y)
    x3 = float(x) + float(y)
    if len(y) == y.count('0'):
        return 0
    if y[0] == '0':
        return int(y)
    try:
        return int(max(x1, x2, x3, float(x) / float(y)))
    except ZeroDivisionError:
        return int(max(x1, x2, x3))
    


print(solve(input()))
