#include <bits/stdc++.h>
#define MAX 1000001
using namespace std;
int prime[MAX];
bool P[MAX];

void save()
{
    P[0] = P[1] = 1;
    for (int i = 2; i * i < MAX; i++) {
        if (P[i] == 0) {
            for (int j = i * i; j < MAX; j+=i) {
                P[j] = 1;
            }
        }
    }
}

string solve()
{
    int n, x;
    scanf("%d", &n);
    
    int arr[n];
    
    int cnt = 0;
    int tt = 0;
    
    for (int i = 0; i < n; i++) {
        scanf("%d", &arr[i]);
        if (arr[i] < 0) {
            cnt++;
            arr[i] = -arr[i];
        }
        if (arr[i] == 0) {
            tt = 1;
        }
    }
    if (tt > 0)
        return "Yes";
    if (cnt % 2 == 1)
        return "No";
    save();
    for (int i = 0; i < n; i++) {
        if (arr[i] == 1) {
            continue;
        }
        if (P[arr[i]] == 0) {
            prime[arr[i]]++;
        }
        else {
            int k = 2;
            int m = arr[i];
            while (m > 1) {
                if (P[m] == 1) {
                    while (m % k == 0) {
                        m /= k;
                        prime[k]++;
                    }
                }
                else {
                    prime[m]++;
                    m = 1;
                }
                k++;
            }
        }
    }
    int temp = 0;
    for (int i = 0; i < MAX; i++) {
        if (prime[i] % 2 == 1)
            return "No";
    }
    return "Yes";
}

int main()
{
    int t = 1;
    while (t--) cout << solve() << endl;
    return 0;
}
