using namespace std;
ostream& operator << (ostream& os, __int128 n){
    string s = "";
    do{
        s = char(n % 10 + 48) + s;
        n /= 10;
    }while(n);
    os << s;
    return os;
}
#define ll __int128
int main(){
  long long _p, _P, INF = 1e10, n, x, t, s, k;
  cin >> _p >> n;
  _P = _p;
  vector<long long>a(1001, 0), b(1001, INF);
  for(int i = 0; i < n; i ++){
    cin >> x;
    a[x] ++;
  }
  cin >> t;
  while(t --){
    cin >> k >> s;
    b[k] = min(s, b[k]);
  }
  for(int i = 1000; i > 0; i --){
    for(int j = i - 1; j >= 0; j --){
      if(b[i] < b[j]) b[j] = b[i];
    }
  }
  ll p = _p, P = _P;
  for(int i = 1; i <= 1000; i ++){
    if(a[i]){
      p -= b[i] * a[i];
    }
  }
  if(p >= 0){
    cout << "Yes\n" << P - p;
  }
  else cout << "No";
  return 0;
}
