#include <bits/stdc++.h>
using namespace std;

int solve()
{
  string str;
  cin >> str;
  
  string s1 = "";
  string s2 = "";
  
  int mx = 1;
  for (int i = 0; i < str.size(); i++) {
    s1 = s1 + str[i];
    s2 = str[i] + s2;
    if (s1 == s2) {
      int m = s1.size();
      mx = max(mx, m);
    }
  }
  return mx;
}
 
int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
    
    int t;
    cin >> t;
    while (t--) cout << solve() << endl;
    return 0;
}
