    int l=0, r=0;
    for ( int i=1; i<s.size(); i++ ){
        if ( r>=i ) v[i]=min(r-i+1,v[i-l]);
        while ( s[i+v[i]]==s[v[i]] && v[i]+i<s.size() ){
