def f(arr, n):
    if len(arr) == 0:
        return 0
    t = arr[n//2]
    return sum([abs(x - t) for x in arr])

n = int(input())
arr = list(map(int,input().split()))
arr.sort()
cnt, m = 0, arr[n // 2]
sm = sum([abs(x - m) for x in arr])
t = 0
while arr:
    a = arr[0]
    while arr:
        if arr[0] == a:
            t += arr.pop(0)
        else:
            break
    k = f(arr, len(arr)) + t
    if sm > k:
        sm = k
    else:
        break
print(sm)
