#include<bits/stdc++.h>
#define int long long
#define md 1000000007
using namespace std;

string solve() {
    int n, m;
    cin >> n >> m;
    int arr[m];
    for (int i = 0; i < m; i++) {
        arr[i] = 0;
    }
    for (int i = 0; i < n; i++) {
        int x;
        cin >> x;
        for (int j = 0; j < x; j++) {
            int y;
            cin >> y;
            arr[y-1]++;
        }
    }
    for (int i = 0; i < m; i++) {
        if (arr[i] == 0) {
            return "NO";
        }
    }
    return "YES";
}

int32_t main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
    int t = 1;
    //cin >> t;
    while (t--) cout << solve() << endl;
    return 0;
}
