#include<bits/stdc++.h>
using namespace std;

void solve() {
    int n;
    cin >> n;
    int arr[n];
    for (int i = 0; i < n; i++) {
        cin >> arr[i];
    }
    int a[n];
    a[0] = 0;
    int out, cur;

    for (int i = 1; i < n; i++){
        out = 0;
        cur = arr[i];
        for(int j = i-1; j >= 0; j--){
            if (cur > arr[j]) {
                out = j + 1; break;
            }
            if (a[j] == 0) break;
            j = a[j];
        }
        a[i] = out;
    }

    for (int i = 0; i < n; i++) {
        cout << a[i] << ' ';
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
    int t = 1;
    //cin >> t;
    while (t--) solve();
    return 0;
}
