def solve():

    n, m, c = map(str,input().split())

    n = int(n)
    m = int(m)
    
    x = bin(n)[2:]
    x = '0' * (16 - len(x)) + x
    
    if c == 'L':
        for i in range(m):
            x = x[1:] + x[0]
    else:
        for i in range(m):
            x = x[-1] + x[:len(x)-1]
    return int(x, 2)
        
test = int(input())
for _ in range(test):
    print(solve())
