#include<bits/stdc++.h>
#define int long long
#define md 1000000007
using namespace std;

int solve() {
    int n, m, k;
    cin >> n >> m >> k;

    int a[n], b[m];
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }
    for (int i = 0; i < m; i++) {
        cin >> b[i];
    }
    sort(a, a + n);
    sort(b, b + m);

    int j = 0;
    int cnt = 0;
    for (int i = 0; i < n; i++) {
        if (a[i]+k<b[j]) continue;
        while (j < m && a[i]-k>b[j]) {
            j++;
        }
        if (j == m) break;
        if (a[i]-k<=b[j]&&b[j]<=a[i]+k)
            cnt++, j++;
    }
    return cnt;

}

int32_t main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
    int t = 1;
    //cin >> t;
    while (t--) cout << solve() << endl;
    return 0;
}
