#include<bits/stdc++.h>
using namespace std;

int solve() {
    int n;
    cin >> n;
    int arr[n];
    for (int i = 0; i < n; i++) {
        cin >> arr[i];
    }
    int l = 1;
    for (int i = 1; i < n; i++) {
        if (arr[i-1] > arr[i]) {
            l++;
        }
    }
    return l;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
    int t;
    cin >> t;
    while (t--) cout << solve() << endl;
    return 0;
}
