#include <bits/stdc++.h>
using namespace std;

string X, Y;

void solve() {
    string s1, s2, s3;
    cin >> s1 >> s2 >> s3;
    map<string, int>mp;
    for (int i = 0; i < s1.size(); i++) {
        X = "";
        for (int j = i; j < s1.size(); j++) {
            X += s1[j];
            if (s2.find(X) != -1 && s3.find(X) != -1) {
                mp[X] = 1;
            }
        }
    }
    cout << mp.size() << endl;
    for (auto it = mp.begin(); it != mp.end(); it++) {
        cout << it->first << endl;
    }
}

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
    int t = 1;
    while (t--) solve();
    return 0;
}
