#include<bits/stdc++.h>
#define mx 1000001
using namespace std;
int Z[mx];

void printZarr(int n) {
    for (int i = 0; i < n; i++) {
        cout << Z[i] << " ";
    }
}

void getZarr() {
    
    string s;
    cin >> s;

    int n = s.size();
    int L, R;
    L = R = 0;

    for (int i = 1; i < n; i++) {
        if (R >= i)
            Z[i] = min(R-i+1, Z[i-L]);
        while (Z[i]+i < n && s[Z[i]] == s[Z[i]+i])
            Z[i]++;
        if (Z[i]+i-1 > R) {
            L = i;
            R = Z[i]+i-1;
        }
    }
    printZarr(n);
}

int main() {

    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

  int t = 1;
  while (t--) getZarr();
  return 0;
}
