#include<bits/stdc++.h>
using namespace std;

string x, y;

int z_function() {

    string s;
    cin >> s;

    int n = s.size();
    int cnt = 0;

    for (int i = 0; i < n-1; i++) {
        map<string,bool> mp;
        x += s[i];
        y = x;
        mp[y] = 1;
        for (int j = i+1; j < n; j++) {
            y += s[j];
            y.erase(0, 1);
            mp[y] = 1;
        }
        cnt += (i+1) * mp.size();
    }
  return cnt + n;
}

int main() {

	int t = 1;
	while (t--) cout << z_function();

	return 0;
}
