#include<bits/stdc++.h>
#define int long long
#define mx 8000
int Z[mx], P[mx];
using namespace std;

void null(int x, int y) {
    for (int i = x; i < y; i++) {
        Z[i] = 0;
    }
}

void print(int n) {
    for (int i = 0; i < n; i++) {
        cout << Z[i] << " ";
    }
    cout << endl;
}

int z_function() {

    string s;
    cin >> s;
    int n = s.size();
    int L, R, K;
    for (int i = 1; i < n; i++) {
        L = R = 0;
        K = i;
        for (int j = 1; j < s.size(); j++) {
            if (R >= j) {
                Z[j] = min(R-j+1, Z[j-L]);
            }
            while (s[j+Z[j]] == s[Z[j]] && Z[j]+j < s.size() ){
                Z[j]++;
            }
            if (Z[j] + j - 1 > R) {
                L = j;
                R = Z[j] + j - 1;
            }
            P[K] = max(P[K], Z[j]);
            K++;
        }
        null(1, n-i+1);
        s.erase(0, 1);
    }
    sort(P, P + n);
    int cnt = 0, c = 1, xx = 1;
    for (int i = 1; i < n; i++) {
        if (P[i-1] == P[i]) {
            c++;
        }
        else {
            cnt = cnt + (c - (xx - 1)) * xx;//000112
            xx++;
            c++;
        }
    }
    cnt = cnt + (c - (xx - 1)) * xx;
    xx++;
    for (int i = xx; i <= n; i++) {
        cnt = cnt + i * (n - i + 1);
    }
    return cnt;

    return 1;

}

int32_t main() {

    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

	int t = 1;
	while (t--) cout << z_function();

	return 0;
}
