#include <bits/stdc++.h>
using namespace std;

#define I(s) int(s)-48
#define C(s) char(s+48)

int solve() {
    int n, k, t;
    cin >> n >> t >> k;
    list < int > mp;
    for (int i = 0; i < n; i++) {
        mp.push_back(t);
    }

    while (k--) {
        int x = mp.front() - 1;
        mp.push_back(x);
        mp.pop_front();
    }

    while (!mp.empty()) {
        int x = mp.front();
        if (x < 1)
            return 1;
        mp.pop_front();
    }
    return -1;

}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    int t = 1;
    //cin >> t;
    while (t--) cout << solve() << endl;
    return 0;
}
