def solve():

    n = int(input())
    x1, y1 = map(int,input().split())
    x2, y2 = map(int,input().split())

    if y1 < x2 or y2 < x1:
        return n - (y1 - x1 + 1) - (y2 - x2 + 1)
    if x1 <= x2 and y2 <= y1:
        return n - (y1 - x1 + 1)
    if x2 <= x1 and y1 <= y2:
        return n - (y2 - x2 + 1)
    if x1 > x2:
        x1, x2, y1, y2 = x2, x1, y2, y1
    return n - (y2 - x1 + 1);

print(solve())
