#include <bits/stdc++.h>
using namespace std;
int arr[5005];

int main()
{
	int n, k;
	cin >> n >> k;
	for (int i = 0; i < k; i++) {
		int x, y;
		cin >> x >> y;
		for (int j=x; j<=y; j++) {
			arr[j]++;
		}
	}
	for (int i=1; i<=n; i++) {
		if(arr[i] != 1) {
			cout << i << " " << arr[i];
			return 0;
		}
	}
	cout << "OK";
	return 0;
}
