#include <bits/stdc++.h>
using namespace std;
vector < int > adj[100];
int arr[100], d;

void dfs(int u) {
	arr[u] = 1, d++;
	for (auto v : adj[u]) {
		if (arr[v] == 0) {
			dfs(v);
		}
	}
}

int main()
{
	int n, k;
	cin >> n >> k;
	for (int i=0; i<k; i++) {
		int u, v;
		cin >> u >> v;
		adj[u-1].push_back(v-1);
		adj[v-1].push_back(u-1);
	}
	dfs(0);
	if (d == n && n == k) {
		cout << "KaydZyu!";
	}
	else {
		cout << "NO";
	}
	
	return 0;
}
