#include<bits/stdc++.h>
#define int long long
using namespace std;
long long n,mn,cr,nt;
double dx,dy,l,bl,bc,c,ans,sp;
double sqr(double x)
{
 return x*x;
}
struct f{
 long long x,y;
}p[200009]; 
double F()
{
 cr = mn;
 dx = -1.0;
 dy = 0.0;
 do{
  bl = 0.0;
  bc = -2.0;
  nt = -1;
  for(int i = 0; i < n; i ++)
  {
   l = sqrt(sqr(p[i].x - p[cr].x + 0.0) + sqr(p[i].y - p[cr].y + 0.0));
   if(!l)
    continue;
   sp = dx * (p[i].x - p[cr].x + 0.0) + dy * (p[i].y - p[cr].y + 0.0);
   c = sp / l;
   if(c > bc || c == bc && l > bl)
    bc = c,
    bl = l,
    nt = i; 
  }
  if(nt < 0)
   return 0;
  ans += bl;
  dx = (p[nt].x - p[cr].x + 0.0) * 1.0 / bl;
  dy = (p[nt].y - p[cr].y + 0.0) *1.0 / bl;
  cr = nt;
 }while(cr != mn);
 return ans;
}
signed main()
{
 cin >> n;
 for(int i = 0; i < n; i ++)
 {
  cin >> p[i].x >> p[i].y;
  if(p[i].y < p[mn].y || p[i].y == p[mn].y && p[i].x < p[mn].x)
   mn = i; 
 }
 printf("%.9lf", F());
}
