#include <bits/stdc++.h>
using namespace std;
int const maxn = 8e6;
bool a[maxn];
int solve() {
    memset(a, 0, sizeof a);
    int n, k;
    cin >> n >> k;
    a[0] = 1;
    int l = 0;
    for(int i = 0; i < n; i ++) {
        int x;
        cin >> x;
        for(int i = 0; i <= k - x; i ++)
            if(a[i])
                a[i + x] = 1;
    }
    while(!a[k])
        k --;
    cout << k << '\n';
    return 0;
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);
    int t;
    cin >> t;
    while(t --) solve(); 
}
