from math import *

def solve():

    arr = [0] * 26
    for i in input(): arr[ord(i) - 97] += 1
    cnt = 0
    for i in range(26):
        if arr[i] % 2 == 1:
            cnt += 1
            if cnt > 1:
                return 0
        arr[i] //= 2
    p = factorial(sum(arr))
    for i in range(26):
        if arr[i] > 0:
            p //= factorial(arr[i])
    return p % 1000000007;

print(solve())
