#include <bits/stdc++.h>
using namespace std;

int solve()
{

    int n, k;
    cin >> n >> k;
    int dp[k+1];

    for (int i = 1; i <= k; i++)
        dp[i] = 0;

    int cnt = n, arr[n];
    for (int i = 0; i < n; i++) {
        cin >> arr[i];
        if (arr[i] > 0 && arr[i] <= k) {
            dp[arr[i]]++;
        }
    }
    for (int i = 0; i < n; i++)
        if (arr[i] > 0 && arr[i] <= k) {
            dp[arr[i]]--;
            if (dp[arr[i]] > 0)cnt--;
            else {dp[arr[i]]++; break;}
        }
        else cnt--;

    for (int i = n-1; i >= 0; i--)
        if (arr[i] > 0 && arr[i] <= k) {
            dp[arr[i]]--;
            if (dp[arr[i]] > 0) cnt--;
            else {dp[arr[i]]++; break;}
        }
        else cnt--;
    return cnt;
}

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    int t;
    cin >> t;
    while (t--) cout << solve() << "\n";
    return 0;
}
