#include <bits/stdc++.h>
using namespace std;

#define I(x) char(x + 48)
#define C(x) int(x - 48)

int solve()
{
    int n;
    cin >> n;
    int arr[n];
    for (int i = 0; i < n; i++)
        cin >> arr[i];
    int cnt = 1, p = 1;
    for (int i = 1; i < n; i++) {
        if (arr[i-1] <= arr[i])
            p++;
        else {
            cnt = max(cnt, p);
            p = 1;
        }
    }
    return max(cnt, p);
}

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    int t = 1;
    while (t--) cout << solve() << endl;

    return 0;
}
