#include<bits/stdc++.h>
using namespace std;
#define int long long

struct point {
    int x, y;
};

#define mx 50000
point b[mx];

signed main() {

    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);

    int n, h;
    cin >> n >> h;
    for (int i = 0; i < n; i++) {
        int u, v;
        cin >> u >> v;
        b[i].x = u;
        b[i].y = v;
    }
    
    sort(b, b + n, [](point u, point v) {
         if (u.x == v.x) return u.y > v.y;
         return u.x < v.x;
         });
    
    bool key = true;
    for (int i = 0; i < n; i++) {
        if (b[i].x > h) {
            key = false;
            break;
        }
        h += b[i].y;
    }
    if (key == false) cout << "NO";
    else cout << "YES";


   return 0;
}
