#include <bits/stdc++.h>
#define mod 99824353
using namespace std;
map<long long, int> dp;
int rec(long long n) {
    if(dp.count(n))
        return dp[n];
    if(n < 5)
        return dp[n] = n;
    long long k = n / 2;
    long long l = n - k;
    int res = rec(k);
    if(l != k)
        return dp[n] = res * 1ll * rec(l) % mod;
    return dp[n] = res * 1ll * res % mod;
}
