#include <bits/stdc++.h>
#define int long long
using namespace std;

int solve()
{
    int n;
    cin >> n;
    int l = 0, r = 0, p = 0;
    for (int i = 0; i < n; i++) {
        string s;
        cin >> s;
        int x = s.find("0");
        int y = s.find("1");

        if (x != -1 && y != -1)
        {
            p++;
        }
        else
        {
            if (x == -1 && y != -1)
            {
                l++;
            }
            else {
                r++;
            }
        }
    }
    int ans = (l + r) * p;
    ans += p * (p - 1) / 2;
    ans += l * (l - 1) / 2;
    ans += r * (r - 1) / 2;
    return ans;


}
int32_t main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);
    int t;
    cin >> t;
    while (t--) {
        cout << solve() << endl;
    }
    return 0;
}
