#include <bits/stdc++.h>
using namespace std;

#define mx 2000000
#define mod 1000000007
bool prime[mx];

void saveprime()
{
    prime[0] = prime[1] = true;
    for (int i = 2; i * i < mx; i++) {
        if (prime[i] == false) {
            for (int j = i * i; j < mx; j += i) {
                prime[j] = true;
            }
        }
    }
}

int solve()
{
    int n;
    cin >> n;
    saveprime();

    int cnt = 0, ans = 0;
    int l = 0, r = n - 1;
    for (int i = 2; i <= r; i++) {
        if (prime[i] == false) {
            cnt++;
        }
    }

    ans = cnt;
    for (int i = 0; i < n-1; i++) {
        l++;
        r++;
        if (prime[l-1] == false) {
            cnt--;
        }
        if (prime[r] == false) {
            cnt++;
        }
        ans = (ans + cnt) % mod;
    }
    return ans;
}

int main()
{
    int t = 1;
    while (t--) cout << solve() << endl;
    return 0;
}
