#include <bits/stdc++.h>
using namespace std;

#define mx (int) 2e+6
long long dp[mx+1];
bool prime[mx+1];

void saveprime()
{
    prime[0] = prime[1] = 1;
    for (int i = 2; i * i <= mx; i++)
    {
        if (prime[i] == 0)
        {
            for (int j = i * i; j <= mx; j += i)
            {
                prime[j] = 1;
            }
        }
    }
}

void fun()
{
    saveprime();
    for (int i = 2; i <= mx; i++)
    {
        long long n = i;
        long long k = 2;
        long long ans = 0;
        while (n > 1)
        {
            if (prime[n] == 0)
            {
                ans++;
                break;
            }
            bool key = false;
            while (n % k == 0)
            {
                n /= k;
                key = true;
            }
            if (key) ans++;
            k++;
        }
        dp[i] = ans;
    }
    for (int i = 2; i < mx; i++)
    {
        dp[i+1] += dp[i];
    }
}

long long solve()
{
    long long n, m;
    cin >> n >> m;
    long long temp = 0;
    long long l = 0, r = m;
    for (int i = 0; i < n; i++)
    {
        l += 1;
        r += 1;
        temp += (dp[r] - dp[l]);
    }
    return temp;
}

int main() {

    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);
    int t;
    cin >> t;
    fun();
    while (t--) cout << solve() << endl;
    return 0;
}
