#include <bits/stdc++.h>
#define int long long
using namespace std;

int f(int n, int k) {
    string x = "4", y = "7", temp = "";
    if (k == 1)
        swap(x, y);


    while (n > 0) {
        if (n % 2 == 1) {
            temp += x;
        }
        else {
            temp += y;
        }
        n /= 2;
    }
    int ans = 0;
    reverse(temp.begin(), temp.end());
    for (int i = 0; i < temp.size(); i++) {
        ans = ans * 10 + temp[i] - 48;
    }

    return ans;
}

int solve() {
    int n;
    cin >> n;
    int i = 1;
    int x = 4, y = 7;
    vector < int > v;
    while (i < 1000) {
        if (x < n) {
            v.push_back(x);
        }
        if (y < n) {
            v.push_back(y);
        }
        x = f(i, 1);
        y = f(i, 2);
        i++;
    }
    set<int>stt;
    for (int i = 0; i < v.size(); i++) {
        stt.insert(v[i]);
    }
    vector<int>u;
    for (auto it=stt.begin(); it!=stt.end(); it++) {
        u.push_back(*it);
    }

    int cnt = 0;
    for (int i = 0; i < u.size()-1; i++) {
        for (int j = i + 1; j < u.size(); j++) {
            if (__gcd(u[i], u[j]) == 1) {
                cnt++;
            }
        }
    }
    return cnt;
}

int32_t main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
    int t = 1;
    while (t--) cout << solve() << endl;
    return 0;
}
