#include <bits/stdc++.h>
using namespace std;

int main(){

    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);

    int n;
    cin >> n;

    char a[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            cin >> a[i][j];
        }
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (i>0 && j>0 && i+1<n && j+1<n && a[i][j]=='o' && a[i-1][j]=='o' && a[i+1][j]=='o' && a[i][j-1]=='o' && a[i][j+1]=='o') {
                a[i][j] = a[i-1][j] = a[i+1][j] = a[i][j-1] = a[i][j+1] = '.';
            }
        }
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (a[i][j] == 'o') {
                return cout << "no", 0;
            }
        }
    }

    cout << "yes";

    return 0;
}
