#include<bits/stdc++.h>
#define int long long
using namespace std;

int pow(int x, unsigned int y, int p) {
	int res = 1;
	x = x % p;
	while (y > 0) {
		if (y & 1)
			res = (res*x) % p;
		y = y >> 1;
		x = (x*x) % p;
	}
	return res;
}

void f(unordered_set<int> &s, int n) {
    int i = 2;
    while (n >= i * i) {
        while (n % i == 0) {
            s.insert(i);
            n /= i;
        }
        i++;
    }
	if (n > 2) s.insert(n);
}

int solve(int n) {
	unordered_set<int> s;
	int k = n-1;
	f(s, k);

	for (int r = 2; r <= k; r++) {
		bool key = false;
		for (auto it = s.begin(); it != s.end(); it++) {
			if (pow(r, k/(*it), n) == 1) {
				key = true;
				break;
			}
		}
		if (key == false)
		return r;
	}
}

int g(int n) {
    int res = 1;
    for (int i = 2; i * i <= n; i++) {
        int c = 0;
        if (n % i == 0) {
            while (n % i == 0) {
                c++;
                n /= i;
            }
        }
        if (c > 0) {
            int pw = (int)pow(i, c - 1);
            int sm = pw * (i - 1);
            res *= sm;
        }
    }
    if (n > 1) {
        res *= (n - 1);
    }
    return res;
}

int32_t main() {
	int p;
	cin >> p;
	cout << solve(p) << " " << g(p - 1);
	return 0;
}
