#include <bits/stdc++.h>
using namespace std;
int main() {
  int n;
  cin >> n;
  vector<double> points(n);
  for (int i = 0; i < n; i++)
    cin >> points[i];
  
  double sum = 0;
  
  for (int i = 0; i < n-1; i++) { 
    sum += points[i] + (points[i+1] - points[i])/2;   
  }
  sum /= (n-1);
  printf("%.10f", sum);
}
