#include<bits/stdc++.h>
enum{mx=100000001};
bool p[mx];
using namespace std;

void save(int a) {
    p[0] = p[1] = 1;
    for (int i = 2; i * i <= a; i++) {
        if (p[i] == 0) {
            for (int j = i * i; j <= a; j+=i) {
                p[j] = 1;
            }
        }
    }
}

int sum_dict(int n) {
    int p = 0;
    while (n != 0) {
        p += n%10;
        n /= 10;
    }
    return p;
}

int solve() {

    int a, b;
    cin >> a >> b;
    if (b >= 99999989 && a <= 99999989)
        return 99999989;
    if (b > 99999989 && a > 99999989)
        return -1;
    save(b);
    int mxx = INT_MIN, m = -1;
    for (int i = b; i >= a; i--) {
        if (p[i] == 0) {
            int x = sum_dict(i);
            if (mxx < x) {
                mxx = x;
                m = i;
            }
        }
    }
    return m;
}
int main() {

    ios_base::sync_with_stdio( 0 );
    cin.tie(NULL);
    cout.tie(NULL);

    int t = 1;
    //cin >> t;
    while (t--) cout << solve() << endl;

	return 0;
}
