#include <bits/stdc++.h>
using namespace std;

int main()
{
    int n;
    cin >> n;
    int arr[n * 2];
    for (int i = 0; i < 2*n; i++) {
        cin >> arr[i];
    }
    n *= 2;
    int cnt = n / 2;
    for (int i = 1; i < n; i++) {
        if (arr[i-1] == arr[i+1]) {
            i++;
            cnt++;
        }
    }
    cout << cnt;

    return 0;

}
