#include<bits/stdc++.h>
using namespace std;
#define mx 300
int p[mx];
int main()
{
    ios_base::sync_with_stdio( 0 );
    cin.tie(NULL);
    cout.tie(NULL);
    int n, m;
    cin >> n >> m;
    vector<int>adj[n];
    for (int i = 0; i < m; i++)
    {
        int u, v;
        cin >> u >> v;
        adj[u-1].push_back(v-1);
        adj[v-1].push_back(u-1);
    }
    int t;
    cin >> t;
    while (t--)
    {
        int x, y;
        cin >> x >> y;
        x--;
        y--;
        int cnt = 0;
        for (int i = 0; i < n; i++)
        {
            if (i == x || i == y)
                continue;
            p[i] = 1;
            list<int>lt;
            p[x] = 1;
            lt.push_back(x);
            while (!lt.empty())
            {
                int u = lt.front();
                lt.pop_front();
                for (auto v : adj[u])
                {
                    if (p[v] == 0)
                    {
                        p[v] = 1;
                        lt.push_back(v);
                    }
                }
            }
            if (p[y] == 0) cnt++;
            for (int ii = 0; ii < n; ii++)
            {
                p[ii] = 0;
            }
        }
        cout << cnt << endl;
    }
    return 0;
}
