#include <bits/stdc++.h>
using namespace std;

#define mx 100000
vector<int>adj[mx];
int p[mx];

void dfs(int u)
{
    for (auto x : adj[u]) {
        if (p[x] == 0) {
            p[x] += p[u] + 1;
            dfs(x);
        }
    }
}

int main()
{
    ios_base::sync_with_stdio( 0 );
    cin.tie(NULL);
    cout.tie(NULL);
    
    int n, m;
    cin >> n >> m;

    for (int i = 0; i < m; i++) {
        int u, v;
        cin >> u >> v;
        adj[u-1].push_back(v-1);
        adj[v-1].push_back(u-1);
    }
    int node;
    for (int i = 0; i < n; i++) {
        if (adj[i].size() == 1) {
            node = i;
            break;
        }
    }
    p[node] = 1;
    dfs(node);
    int t;
    cin >> t;
    while (t--)
    {
        int u, v;
        cin >> u >> v;
        cout << abs(p[u - 1] - p[v - 1]) - 1 << endl;
    }
    return 0;

}
