#include<bits/stdc++.h>
using namespace std;
#define mx 100001
bool p[mx];
vector<int>adj[mx];
int d = 1;

void dfs(int u)
{
    p[u] = 1;
    for (auto x : adj[u]) {
        if (p[x] == 0) {
            d++;
            dfs(x);
        }
    }
}

int main()
{
    ios_base::sync_with_stdio( 0 );
    cin.tie(NULL);
    cout.tie(NULL);

    int n;
    cin >> n;
    for (int i = 0; i < n-1; i++) {
        int u, v;
        cin >> u >> v;
        adj[u-1].push_back(v-1);
        adj[v-1].push_back(u-1);
    }
    dfs(0);
    if (d == 1) cout << 0;
    else {
        cout << n;
    }
}
