#include <iostream>
#include<map>
using namespace std;

int main()
{
	int n, k;
	cin >> n >> k;
	map<int,int>mp;

	for (int i = 0; i < n; i++) {
        int x;
        cin >> x;
        if (mp.find(x) == mp.end()) {
            mp[x] = 1;
        }
        else {
            mp[x]++;
        }
	}
    int m = mp.size();
    int a[m], b[m], l = 0;
	for (auto x = mp.begin(); x != mp.end(); x++) {
        a[l] = x->first;
        b[l] = x->second;
        l++;
	}
    int res = 0;
	for (int i = 0; i < m; i++) {
        for (int j = i + 1; j < m; j++) {
            if (a[i]-a[j] == k || a[j]-a[i] == k) {
                res += 1;
            }
        }
        if (b[i] > 1 && k==0) res += 1;
	}
	cout << res;

	return 0;
}
