#include<iostream>
#include<vector>
using namespace std;

#define mx 100000
bool p[mx];

void save()
{
	for (int i = 2; i*i < mx; i++)
	{
		if (p[i] == 0)
		{
			for (int j = i * i; j < mx; j += i)
			p[j] = 1;
		}
	}
}

int main()
{
	save();
	int n;
	cin >> n;
	vector<int>a;
	for (int i=2;i<mx;i++)
	if (p[i] == 0) a.push_back(i);
	
	for (int i = 1; i < a.size(); i++) {
		a[i] += a[i-1];
	}
	for (int i = 1; i < a.size(); i++) {
		a[i] += a[i-1];
	}
	cout << a[n-1];
}
