def solve(a, b):
	s = ''
	i = 0
	z = 0
	while len(a) > i and len(b) > i:
		i += 1
		x = int(a[-i])
		y = int(b[-i])
		if x + y + z < 7:
			s += str(x + y + z)
			z = 0
		else:
			s += str((x + y + z) % 7)
			z = (x + y + z) // 7
	if len(a) == i:
			while len(b) > i:
				x = int(b[-i])
				if x + z < 7:
					s += str(x + z)
					z = 0
				else:
					s += str((x + z) % 7)
					z = (x + z) // 7
				i += 1
			if z > 0:
				s += str(z)
	else:
			while len(a) > i:
				x = int(a[-i])
				if x + z < 7:
					s += str(x + z)
					z = 0
				else:
					s += str((x + z) % 7)
					z = (x + z) // 7
				i += 1
			if z > 0:
				s += str(z)
	return s[::-1]
			
		
