#include <bits/stdc++.h>
#define int long long
using namespace std;

#define mx 10000000
bool p[mx];

void save()
{
    p[0] = p[1] = 1;
    for (int i = 2; i * i < mx; i++)
    {
        if (p[i] == 0)
        {
            for (int j = i * i; j < mx; j += i)
            {
                p[j] = 1;
            }
        }
    }
}

bool isprime(int n)
{
    if (n == 1) return 0;
    for (int i = 2; i * i <= n; i++)
    {
        if (n % i == 0)
            return 0;
    }
    return 1;
}

int32_t main() {

    int n;
    save();
    cin >> n;

    int i = 2;
    int cnt = 0;
    
    while (n > 1 && i < mx)
    {
        bool k = 0;
        while (p[i] == 1) i++;
        while (n % i == 0)
        {
            n /= i;
            k = 1;
        }
        if (k) cnt++;
        i++;
    }
    if (n < 1e13 && isprime(n)) cnt++;
    else
    {
        if (n > 1e13) cnt += 2;
        else
        if (n > 1) cnt++;
    }
    cout << cnt;

    return 0;
}
