def solution (A, K):

    count = 0

    for i in range(len(A)):

        ex=0

        while "P" in A[i]:

            Pj = A[i].index("P")

            if "Q" in A[i]:

                Tj = A[i].index("Q")

                dist = abs(Tj-Pj)

                ex = min(Tj,Pj)

                if dist<=K:

                    count+=1

                    A[i][Pj] = "X"

                    A[i][Tj] = "X"

                    A[i] = A[i][ex+1:]

                else:

                    A[i][ex] = "X"

            else:

                break
    
    return count
T = int(input())
for _ in range(T):
    N, K = map(int, input().split())
    A = []
    for _ in range(N):
        A.append(input().split())
    out_ = solution(A, K)
    print (out_)

