#include <bits/stdc++.h>
using namespace std;

int arr[10000], m;

int32_t main () {

    int n;
    cin >> n;
    if (n == 1)
        return cout << 1, 0;

    int i = 2;
    while (n > 1)
    {
        int a = 0;
        while (n % i == 0)
        {
            n /= i;
            a++;
        }
        if (a > 0)
        {
            arr[m++] = a;
        }
        i++;
    }
    int key = 0;
    for (int i = 0; i < m; i++)
    {
        if (arr[i] > 1)
        {
            key = 1;
            break;
        }
    }
    if (key == 1)
        cout << 0;
    else
        if (m % 2 == 0)
            cout << 1;
        else
            cout << -1;

	return 0;
}
