using namespace std;
typedef long long ll;
typedef pair<int, int> ii;
typedef vector<ii> vii;
typedef vector<int> vi;
const double EPS = 1E-9;
int a[512][512];
int p[100000];
void build(int n) {
    if(n == 1){
        a[0][0]=1;
        return;
    }
    int n2 = n/2;
    build(n2);
    for(int q = n2; q < n;++q)
        for(int u = n2; u < n;++u)
            a[q][u]=a[q-n2][u-n2];
 
    for(int q = 0; q < n2;++q)
        for(int u = 0; u < n2;++u)
            a[q+n2][(u+q)%n2] = u + n;
 
    for(int q = 0; q <n2; ++q)
        for(int u = 0; u < n2; ++u)
        a[q][(q+u)%n2+n2] = u+n+n2;
} 
void solve() {
    int k, n;  cin >> k;
    n = 1 << k;
    build(n);
    for(int i = 0; i < n; ++i)
        for(int j = 0; j < n; ++j)
        cout << a[i][j] << " \n"[j==n-1];
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    int t = 1;
    while(t--)
        solve();
    return 0;
}
