#include<bits/stdc++.h>
#define int long long
using namespace std;

#define mx 1005
int dp[mx][mx];
signed main() 
{
  ios_base::sync_with_stdio(false);

cin.tie(NULL);
  cout.tie(NULL);
	int n, m;
	int mod = 1000000007;
	cin >> n >> m;
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < m; j++) {
		  char x;
		  cin >> x;
		  dp[i][j] = (x == '1') ? -1 : 0;
		}
	}
  if (dp[0][0]==-1 || dp[n-1][m-1]==-1) return cout<<0, 0;
	dp[0][0] = 1;
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < m; j++) {
			if (dp[i][j] != -1) {
			int y = j;
			while (y+1<m && dp[i][y+1]==-1) {
				y++;
			}
			if (y+1<m) 
			dp[i][y+1] = (dp[i][y+1]+dp[i][j])%mod;
		
			int x = i;
			while (x+1<n && dp[x+1][j]==-1) {
				x++;
			}
			if (x+1<n) 
			dp[x+1][j] = (dp[x+1][j] + dp[i][j]) % mod;
			}
		}
	}
	
	cout << dp[n-1][m-1] % mod;
	
	return 0;
}
