#include <bits/stdc++.h>
#define int long long
using namespace std;

int32_t main () {

    string s;
    cin >> s;
    int x = 0;
    int cnt = 0;
    int k = 0;
    for (int i = 0; i < s.size(); i++) {
        if (s[i] == 'L')
        {
            x++;
            if (x == 1 && k == 0)
            {

                cnt++;
            k = 1;
            }
        }
        else
        {
            x--;
            k = 0;
        }
    }
    cout << cnt;

	return 0;
}
